mapper.select:
select * from device_maintenance
where device_id = #{deviceId}
and isactive = '0'


    and username = #{myusername}


    and `date` >= #{startDate}


    and `date` <= #{endDate}


    and reported = #{reported}

select device_maintenance.id, device_maintenance.device_id, device_maintenance.batch, sum(num) num, device_maintenance.other_type, device_maintenance.other_num, device_maintenance.username, user.name, device_maintenance.date, device_maintenance.longitude, device_maintenance.latitude, device_maintenance.altitude, device_maintenance.img_id, device_maintenance.drug, device_maintenance.remark, device.province, device.city, device.area, device.town, device_maintenance.working_content, device_maintenance.reported from device_maintenance left join device on device.id = device_maintenance.device_id left join user on device_maintenance.username = user.username where device_id in (select id from device where 1 = 1
and adcode like concat(#{adcode}, '%')
and town = #{town}
)
and ( user.username like concat('%', #{condition}, '%') or device.province like concat('%', #{condition}, '%') or device.city like concat('%', #{condition}, '%') or device.area like concat('%', #{condition}, '%') or device.town like concat('%', #{condition}, '%') or user.username like concat('%', #{condition}, '%') or user.name like concat('%', #{condition}, '%') or device_maintenance.device_id like concat('%', #{condition}, '%') )
and `date` >= #{date}
and `date` <= #{endDate}
and reported = #{reported}
and isactive = '0'
group by device_id order by `date` desc

select device_maintenance.id, device_maintenance.device_id, device_maintenance.batch, num, device_maintenance.other_type, device_maintenance.other_num, device_maintenance.username, user.name, device_maintenance.date, device_maintenance.longitude, device_maintenance.latitude, device_maintenance.altitude, device_maintenance.img_id, device_maintenance.drug, device_maintenance.remark, device.province, device.city, device.area, device.town, device_maintenance.working_content, device_maintenance.reported from device_maintenance left join device on device.id = device_maintenance.device_id left join user on device_maintenance.username = user.username where device_id in (select id from device where 1 = 1
and adcode like concat(#{adcode}, '%')
and town = #{town}
)
and ( user.username like concat('%', #{condition}, '%') or device.province like concat('%', #{condition}, '%') or device.city like concat('%', #{condition}, '%') or device.area like concat('%', #{condition}, '%') or device.town like concat('%', #{condition}, '%') or user.username like concat('%', #{condition}, '%') or user.name like concat('%', #{condition}, '%') or device_maintenance.device_id like concat('%', #{condition}, '%')
)
and `batch` = #{batch}
and `date` >= #{date}
and device.town like concat('%', #{searchtown}, '%')
and `date` <= #{endDate}
and reported = #{reported}
and isactive = '0'
order by `date` desc

select device_maintenance.id, device_maintenance.device_id, device_maintenance.batch, sum(num) num, device_maintenance.other_type, device_maintenance.other_num, device_maintenance.username, user.name, device_maintenance.date, device_maintenance.longitude, device_maintenance.latitude, device_maintenance.altitude, device_maintenance.img_id, device_maintenance.drug, device_maintenance.remark, device.province, device.city, device.area, device.town, device_maintenance.working_content, device_maintenance.reported from device_maintenance left join device on device.id = device_maintenance.device_id left join user on device_maintenance.username = user.username where device_id in (select id from device where 1 = 1
and adcode like concat(#{adcode}, '%')
and town = #{town}
)
and ( user.username = #{condition} or device.province = #{condition} or device.city = #{condition} or device.area = #{condition} or device.town = #{condition} or device_maintenance.device_id = #{condition} or device.manager=#{condition} )
and `date` >= #{date}
and `date` <= #{endDate}
and reported = #{reported}
and isactive = '0' group by device_id,device_maintenance.username
order by `date` desc

select device_maintenance.id, device_maintenance.device_id, device_maintenance.batch, num, device_maintenance.other_type, device_maintenance.other_num, device_maintenance.username, user.name, device_maintenance.date, device_maintenance.longitude, device_maintenance.latitude, device_maintenance.altitude, device_maintenance.img_id, device_maintenance.drug, device_maintenance.remark, device.province, device.city, device.area, device.town, device_maintenance.working_content, device_maintenance.reported from device_maintenance left join device on device.id = device_maintenance.device_id left join user on device_maintenance.username = user.username where device_id in (select id from device where 1 = 1
and adcode like concat(#{adcode}, '%')
and town is not null and town = #{town}
)
and ( user.username = #{condition} or device.province = #{condition} or device.city = #{condition} or device.area = #{condition} or device.town = #{condition} or device_maintenance.device_id = #{condition} or device.manager=#{condition} )
and `date` >= #{date}
and batch= #{batch}
and device.town like concat('%', #{searchTown}, '%')
and `date` <= #{endDate}
and reported = #{reported}
and isactive = '0'
order by `date` desc

select device_maintenance.id, device_maintenance.device_id, device_maintenance.batch, device_maintenance.num, device_maintenance.male_num, device_maintenance.female_num, device_maintenance.username, user.name, device_maintenance.date, device_maintenance.longitude, device_maintenance.latitude, device_maintenance.altitude, device_maintenance.img_id, device_maintenance.drug, device_maintenance.remark, device.province, device.city, device.area, device.town from device_maintenance left join device on device.id = device_maintenance.device_id left join user on user.username = device_maintenance.username where device_maintenance.username = #{username} and isactive = '0'
and ( user.username like concat('%', #{condition}, '%') or device.province like concat('%', #{condition}, '%') or device.city like concat('%', #{condition}, '%') or device.area like concat('%', #{condition}, '%') or device.town like concat('%', #{condition}, '%') or user.username like concat('%', #{condition}, '%') or user.name like concat('%', #{condition}, '%') or device_maintenance.device_id like concat('%', #{condition}, '%') )
and `date` >= #{date}
and `date` <= #{endDate}
order by `date` desc

mapper.update:
update device_maintenance
set reported = 1
where id = #{id}

mapper.delete:
update device_maintenance
set isdelete = '1'
where id = #{id}

mapper.select:
select sum(num) num,DATE_FORMAT(`date`, '%m') `month` from device_maintenance left join device on device_id=device.id where adcode like concat(#{adcode}, '%') and isactive = '0'
and `date` IS NOT NULL
and `date` >= #{startYear}
and `date` <= #{endYear}
group by date_format(`date`, '%m')

select sum(num) num,DATE_FORMAT(`date`, '%m') `month` from device_maintenance left join device on device_id=device.id where adcode like concat(#{adcode}, '%') and isactive = '0'
and `date` IS NOT NULL
and `date` >= #{startYear}
and `date` <= #{endYear}
group by date_format(`date`, '%m')

select sum(num) num,DATE_FORMAT(`date`, '%m') `month`
from device_maintenance
left join device on device_id=device.id
where adcode = #{adcode}
and isactive = '0'

and `date` IS NOT NULL

    and `date` >= #{startYear}




    and `date` <=#{endYear}


group by date_format(`date`, '%m')

select sum(num) num,DATE_FORMAT(`date`, '%m') `month`
from device_maintenance

left join device on device_id=device.id
left join user on user.username = device_maintenance.username
where user.parent = #{adcode}
and isactive = '0'

and `date` IS NOT NULL

    and `date` >= #{startYear}




    and `date` <=#{endYear}


group by date_format(`date`, '%m')

select count(*) from device_maintenance
where device_id = #{id}

and isactive = '0'

select device_maintenance.device_id, device_maintenance.batch, device_maintenance.num, device_maintenance.male_num, device_maintenance.female_num, device_maintenance.username, device_maintenance.other_type, device_maintenance.date, device_maintenance.longitude, device_maintenance.latitude, device_maintenance.altitude, device_maintenance.img_id, device_maintenance.drug, device_maintenance.remark, device.province, device.city, device.area, device.town, device_maintenance.other_num, device_maintenance.working_content
from device_maintenance left join device on device.id = device_maintenance.device_id
where device_maintenance.id = #{id} and isactive = '0'
and `date` >= #{startDate}
and `date` <= #{endDate}

select * from device_maintenance
where device_id = #{id}
      and isactive = '0'


    and `date` >= #{startDate}



    and `date` <= #{endDate}


    and reported = #{reported}

select * from device_maintenance
where device_id = #{id}
and isactive = '0'

select * from device_maintenance_abnormal
where id = #{id}
and isactive = '0'

select * from device_maintenance
where id = #{id}
and isactive = '0'

select town name, sum(num1) `sum`, avg(num1) mean, stddev_samp(num1) `std`, max(num1) `max`, min(num1) `min`, count(distinct device_id) `count`
from (
select town,sum(num) num1,device_id
from device_maintenance
left join device on device_id = device.id
where adcode = #{adcode}
      and isactive = '0'


    and `date` >= #{startDate}



    and `date` <= #{endDate}

group by device_id)as a
group by town

select area name, sum(num1) `sum` , avg(num1) mean, stddev_samp(num1) `std`, max(num1) `max`, min(num1) `min`, count(distinct device_id) `count` from ( select area,sum(num) num1,device_id,adcode from device_maintenance left join device on device_id = device.id where adcode like concat(#{code}, '%') and isactive = '0'
and `date` >= #{startDate}
and `date` <= #{endDate}
group by device_id)as a
group by area

select city name, sum(num1) `sum` , avg(num1) mean, stddev_samp(num1) `std`, max(num1) `max`, min(num1) `min`, count(distinct device_id) `count` from ( select city,sum(num) num1,device_id from device_maintenance left join device on device_id = device.id where adcode like concat(#{code}, '%') and isactive = '0'
and `date` >= #{startDate}
and `date` <= #{endDate}
group by device_id)as a group by city

select username name, sum(num1) `sum` ,avg(num1) mean, stddev_samp(num1) `std`, max(num1) `max`, min(num1) `min`, count(distinct device_id) `count`
from (
select device_maintenance.username username,sum(num) num1,device_id
from device_maintenance
left join device on  device.id= device_maintenance.device_id
where device.manager = #{manager}
      and isactive = '0'


    and `date` >= #{startDate}



    and `date` <= #{endDate}

group by device_id)as a
group by username

select device_maintenance.id, device_maintenance.device_id, device_maintenance.batch, sum(num) num, device_maintenance.other_type, device_maintenance.other_num, device_maintenance.username, user.name, device_maintenance.date, device_maintenance.longitude, device_maintenance.latitude, device_maintenance.altitude, device_maintenance.img_id, device_maintenance.drug, device_maintenance.remark, device.province, device.city, device.area, device.town, device_maintenance.working_content, device_maintenance.reported from device_maintenance left join device on device.id = device_maintenance.device_id left join user on device_maintenance.username = user.username where device_id in (select id from device where 1 = 1
and adcode like concat(#{adcode}, '%')
and town = #{town}
)
and ( user.username like concat('%', #{condition}, '%') or device.province like concat('%', #{condition}, '%') or device.city like concat('%', #{condition}, '%') or device.area like concat('%', #{condition}, '%') or device.town like concat('%', #{condition}, '%') or user.username like concat('%', #{condition}, '%') or user.name like concat('%', #{condition}, '%') or device_maintenance.device_id like concat('%', #{condition}, '%') )
and `date` >= #{date}
and `date` <= #{endDate}
and manager = #{manager}
and isactive = '0'
group by device_id order by `date` desc

