mapper.select:
select count(distinct device_id ) deviceCount, sum(num) beetleCount, device.city `name`, device.adcode code from device_maintenance left join device on device_maintenance.device_id = device.id where adcode like concat(#{adcode}, '%') and isactive = '0'
and device.city is not null
and `date` >= #{startDate}
and `date` <= #{endDate}
group by device.city

select count(distinct device_id) deviceCount, sum(num) beetleCount, device.area `name`, device.adcode code from device_maintenance left join device on device_maintenance.device_id = device.id where adcode like concat(#{adcode}, '%') and isactive = '0'
and device.area is not null
and `date` >= #{startDate}
and `date` <= #{endDate}
group by device.area

select count(distinct device_id) deviceCount, sum(num) beetleCount, device.town `name`, device.adcode code from device_maintenance left join device on device_maintenance.device_id = device.id where adcode like concat(#{adcode}, '%') and isactive = '0'
and device.town is not null
and `date` >= #{startDate}
and `date` <= #{endDate}
group by device.town

select count(distinct device_id) deviceCount, sum(num) beetleCount, device.manager `name`, device.adcode code from device_maintenance left join device on device_maintenance.device_id = device.id where device.adcode like concat(#{adcode}, '%') and isactive = '0'
and device.manager is not null
and `date` >= #{startDate}
and `date` <= #{endDate}
group by device.manager

select count(distinct device_id) deviceCount, sum(num) beetleCount, username `name`, username code from device_maintenance left join device on device_maintenance.device_id = device.id where adcode like concat(#{adcode}, '%') and isactive = '0'
and username is not null
and `date` >= #{startDate}
and `date` <= #{endDate}
group by username

select count(distinct device_id) `count`, sum(num) `sum` from device_maintenance
left join device on device.id = device_maintenance.device_id where adcode like concat(#{adcode}, '%') and isactive = '0'
and `date` >= #{startDate}
and `date` <= #{endDate}

select count(distinct device_id) `count`, sum(num) `sum` from device_maintenance
left join device on device.id = device_maintenance.device_id
left join user on device_maintenance.username = user.username
where parent = #{adcode}
      and isactive = '0'


    and `date` >= #{startDate}


    and `date` <= #{endDate}

select count(distinct device_id) deviceCount, sum(num) beetleCount, device_maintenance.username `name`, device_maintenance.username code
from device_maintenance
left join device on device_maintenance.device_id = device.id
left join user on device_maintenance.username = user.username
where parent = #{manager}
      and isactive = '0'


    and `date` >= #{startDate}



    and `date` <= #{endDate}

group by device_maintenance.username
